import React, { Component } from "react";
import { connect } from "react-redux";
import { Row, TextInput, Textarea, Button } from "react-materialize";

function mapStateToProps(state) {
  return {};
}

class AccountForm extends Component {
  state = {
    account: {},
    edited: false,
    successMessage: "",
    errorMessage: ""
  };
  componentDidMount() {
    if (this.props.account) {
      let account = this.props.account;
      account.price = account.price.$numberDecimal;
      this.setState({
        ...this.state,
        account
      });
    }
  }
  render() {
    const { account, edited } = this.state;
    console.log(account);
    return (
      <div>
        <form>
          <Row>
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Domain:"
              name="domain"
              value={account.domain}
              //    onChange={this.handleChangeProject}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Username:"
              name="username"
              value={account.username}
              //    onChange={this.handleChangeProject}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Password:"
              name="password"
              value={account.password}
              //    onChange={this.handleChangeProject}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Start Date:"
              name="startDate"
              value={account.startDate}
              //    value={project.name}
              //    onChange={this.handleChangeProject}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Expire Date:"
              name="expireDate"
              value={account.expireDate}
              //    value={project.name}
              //    onChange={this.handleChangeProject}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Price:"
              name="price"
              value={account.price}
              //    value={project.name}
              //    onChange={this.handleChangeProject}
              required
            />
            <Textarea
              s={12}
              l={12}
              m={12}
              xl={12}
              label="Description:"
              name="description"
              value={account.description}
              //    value={project.name}
              //    onChange={this.handleChangeProject}
              required
            />
          </Row>
          <Row>
            <Button
              node="button"
              className={edited ? "gradient-btn btn-primary" : ""}
              waves="green"
              disabled={!edited}
            >
              Save
            </Button>
          </Row>
        </form>
      </div>
    );
  }
}

export default connect(mapStateToProps)(AccountForm);
