import React, { Component } from "react";
import { connect } from "react-redux";
import { TextInput, Icon, Button, Row } from "react-materialize";
import axios from "axios";

class ContactForm extends Component {

    constructor(props){
        super(props)
        this.state = {
            contact: {

            }
        }
    }

    handleCreateContact = (e) => {
        e.preventDefault()

        const contact = this.state.contact
        console.log(contact)
        axios.post(`contacts`, contact).then(response => {
            if(response.data.duplicated){
                this.setState({
                    dublicated: true,
                    dublicatedContact: response.data.contact
                })

            } else {
                this.setState({
                    successMessage: 'Save successfully'
                })
            }
        }).catch(error => {

            this.setState({
                errorMessage: 'Cannot save the data'
            })
        })
    }

    handleInputChange = (e) => {
        const phoneRegex = /^\(?([0-9]{3})\)?[-.]?([0-9]{3})[-.]?([0-9]{4})$/
        const field = e.target.name 
        let value = e.target.value 

        if(field === 'phone'){
            value = value.replace(phoneRegex, '($1) $2-$3')
        }

        let contact = {
            ...this.state.contact,
            [field]: value
        }

        this.setState({
            contact
        })

    }

    render() {
        const contact = this.state.contact || {name: '', phone: '', email: ''}

        return (
            <div>
                <form onSubmit={this.handleCreateContact}>
                    <Row>
                        <TextInput
                            s={12}
                            l={2}
                            m={2}
                            xl={2}
                            label='Name'
                            name='name'
                            value={contact.name}
                            required
                            onChange={this.handleInputChange}
                        />
                        <TextInput
                            s={12}
                            l={3}
                            m={3}
                            xl={3}
                            label='Phone (no space, no +1)'
                            name='phone'
                            value={contact.phone}
                            required
                            onChange={this.handleInputChange}
                        />
                        <TextInput
                            s={12}
                            l={6}
                            m={4}
                            xl={4}
                            label='Email'
                            email
                            validate
                            value={contact.email}
                            name='email'
                            onChange={this.handleInputChange}
                        />
                        <Button
                            node='button'
                            type='submit'
                            waves='light'
                            style={{marginTop: '20px'}}
                        >
                            Save
                            <Icon left>save</Icon>
                        </Button>
                    </Row>
                    <Row>
                        {this.state.successMessage && <p className="green-text">{this.state.successMessage}</p>}
                    </Row>

                        {this.state.dublicated && <Row>
                            <h6 className="red-text">A contact has been found with this phone number</h6>
                            <p>Name: {this.state.dublicatedContact.name}</p>
                            <p>Phone: {this.state.dublicatedContact.phone}</p>
                            <p>Email: {this.state.dublicatedContact.email}</p>
                            <Row>
                                <Button>Use this contact</Button>
                                <Button className=" black-text grey lighten-4" style={{marginLeft: "20px"}}>Cancel</Button>
                            </Row>
                        </Row>}

                </form>
            </div>
        );
    }
}

const mapStateToProps = state => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(ContactForm);
