import React, { Component } from "react";
import { connect } from "react-redux";
import {Select} from "react-materialize";

function mapStateToProps(state) {
  return {};
}

class ContributorDropDown extends Component {
  displayContributor = (contributors) => () => {
      return contributors.map(contributor => {
          return <option key={contributor._id} value={contributor._id}>
              {contributor.name}
          </option>
      })
  }
  render() {
        const {contributors} = this.props
        console.log(contributors)
    return (
      <div>
        <Select
          onChange={function noRefCheck() {}}
          options={{
            classes: "",
            dropdownOptions: {
              alignment: "left",
              autoTrigger: true,
              closeOnClick: true,
              constrainWidth: true,
              container: null,
              coverTrigger: true,
              hover: false,
              inDuration: 150,
              onCloseEnd: null,
              onCloseStart: null,
              onOpenEnd: null,
              onOpenStart: null,
              outDuration: 250
            }
          }}
          value=""
        >
          <option disabled value="">
            Choose your option
          </option>
            {this.displayContributor(contributors)}
        </Select>
      </div>
    );
  }
}

export default connect(mapStateToProps)(ContributorDropDown);
