import React, { Component } from "react";
import { TextInput, Row, Col, Button, Icon } from "react-materialize";
import Loading from "../loading/Loading";
import axios from "axios";

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            password: "",
            loading: false,
            errorMessage: ""
        };
    }

    handleChange = e => {
        this.setState({
            ...this.state,
            [e.target.name]: e.target.value
        });
    };

    handleSubmit = e => {
        const { email, password } = this.state;
        this.setState({
            loading: true
        });
        e.preventDefault();
        axios
            .post(`http://localhost:3000/users/login`, {
                email,
                password,
                name: "Ha Nguyen Phu",
                phone: "204 558 4030",
                role: "admin"
            })
            .then(response => {
                console.log(response.data);
                this.setState({
                    loading: false
                });
            })
            .catch(error => {
                this.setState({
                    loading: false
                });
            });
        // fetch(`http://localhost:3000/users`, {
        //     method: 'POST',
        //     headers: {
        //         'Accept': 'application/json',
        //         'Content-Type': 'application/json',
        //     },
        //     body: JSON.stringify({
        //         email,
        //         password,
        //         name: 'Ha Nguyen Phu',
        //         phone: '204 558 4030',
        //         role: 'admin'

        //     })
        // }).then(res => {
        //     return res.json()
        // }).then (data => {
        //     const authToken = data.token
        //     const user = data.user
        //     localStorage.setItem('token', authToken)
        //     localStorage.setItem('user', user.email)
        //     this.setState({
        //         loading: false
        //     })
        // })
    };

    render() {
        const { loading, errorMessage } = this.state;
        return (
            <div style={{ marginTop: "70px" }}>
                <Row>
                    <Col m={4}></Col>
                    <Col m={4}>
                        <h3> Login</h3>
                    </Col>
                    <Col m={4}></Col>
                </Row>

                <form
                    method='POST'
                    onSubmit={this.handleSubmit}
                    className='col s12'
                >
                    <TextInput
                        name='email'
                        email
                        label='Email'
                        validate
                        required
                        onChange={this.handleChange}
                    />

                    <TextInput
                        label='Password'
                        name='password'
                        password
                        required
                        onChange={this.handleChange}
                    />
                    {!loading ? (
                        <Button node='button' waves='light'>
                            Login
                            <Icon right>send</Icon>
                        </Button>
                    ) : (
                        <Loading />
                    )}
                    {errorMessage && <div className="red-text text-accent-2">

                    </div>}
                </form>
            </div>
        );
    }
}
export default Login;
