import {
    AUTHENTICATE,
    LOGOUT,
    UPDATE_PROFILE,
    FETCH_PROVINCE
} from "../constants/action-types";
import axios from "axios";

const initialState = {
    user: {
        authenticated: false
    },
    provinces:[]
};

function rootReducer(state = initialState, action) {
    if (action.type === AUTHENTICATE) {
        //this check the token in localStorage and set the default values for authenticated request from client
        if (localStorage.getItem("user") && localStorage.getItem("token")) {
            //const token = localStorage.getItem("token");
            // axios.defaults.baseURL = 'http://localhost:3000/'
            // axios.defaults.headers.common= {'Authorization': `Bearer ${token}`}

            return Object.assign({}, state, {
                user: {
                    authenticated: action.payload.authenticated,
                    email: action.payload.user.email,
                    name: action.payload.user.name,
                    phone: action.payload.user.phone
                }
            });
        }
    }

    // Update profile in Profile Page
    if (action.type === UPDATE_PROFILE) {
        return Object.assign({}, state, {
            user: {
                ...state.user,
                email: action.payload.email,
                name: action.payload.name,
                phone: action.payload.phone
            }
        });
    }

    if(action.type === FETCH_PROVINCE) {
        console.log('Fetch Province reducer called')
        return Object.assign({}, state, {
            provinces: action.payload.provinces
        })
    }

    //logout the user and delete the token in localStorage and in the server as well
    if (action.type === LOGOUT) {
        localStorage.clear();
        axios
            .post(`users/logout`)
            .then(response => {
                axios.defaults.baseURL = "";
                axios.defaults.headers.common = { Authorization: `` };
            })
            .catch(error => {});
        return Object.assign({}, state, {
            user: {
                authenticated: false
            }
        });
    }
    return state;
}

export default rootReducer;
