import React, { Component } from "react";
import { connect } from "react-redux";
import {
    Modal,
    Button,
    Row,
    Col,
    TextInput,
    Select,
    Table, Icon
} from "react-materialize";
import Loading from "../loading/Loading";
import axios from "axios";

class BusinessModalDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    componentDidMount() {
        this.setState({
            business: this.props.business
        });
        this.getBusinessDetail(this.props.business._id);
    }

    getBusinessDetail(id) {
        this.setState({
            ...this.state,
            loading: true
        });
        axios
            .get(`businesses/${id}`)
            .then(response => {
                if (response.data.businesses) {
                    this.setState({
                        ...this.state,
                        contacts: response.data.businesses.contacts,
                        province: response.data.businesses.province,
                        loading: false
                    });
                }
            })
            .catch(err => {
                this.setState({
                    loading: false
                });
            });
    }

    handleChangeBusiness = e => {
        console.log(e.target.value);
    };

    displayProvince(_id) {
        const { provinces } = this.props;
        const province = provinces.filter(province => province._id === _id)[0];
        return (
            <Select
                options={{
                    classes: "",
                    dropdownOptions: {
                        alignment: "left",
                        autoTrigger: true,
                        closeOnClick: true,
                        constrainWidth: true,
                        container: null,
                        coverTrigger: true,
                        hover: false,
                        inDuration: 150,
                        onCloseEnd: null,
                        onCloseStart: null,
                        onOpenEnd: null,
                        onOpenStart: null,
                        outDuration: 250
                    }
                }}
                onChange={this.handleChangeBusiness}
                value={province._id}
                name='province'
            >
                <option disabled value=''>
                    Select Province
                </option>
                {provinces.map(province => {
                    return (
                        <option
                            className='black-text'
                            key={province._id}
                            value={province._id}
                        >
                            {province.name}
                        </option>
                    );
                })}
            </Select>
        );
    }

    render() {
        const { business } = this.props;
        const contacts = this.state.contacts || [];
        return (
            <Modal
                actions={[
                    <Button flat modal='close' node='button' waves='green'>
                        Close
                    </Button>
                ]}
                open={true}
                style={{ boder: "none" }}
                bottomSheet={false}
                fixedFooter={false}
                header='Business Detail'
                id='modal1'
                options={{
                    dismissible: true,
                    endingTop: "10%",
                    inDuration: 250,
                    onCloseEnd: null,
                    onCloseStart: null,
                    onOpenEnd: null,
                    onOpenStart: null,
                    opacity: 0.5,
                    outDuration: 250,
                    preventScrolling: true,
                    startingTop: "4%"
                }}
            >
                {!this.state.loading ? (
                    <div>
                        <form>
                            <Row>
                                <TextInput
                                    s={12}
                                    l={6}
                                    m={6}
                                    xl={6}
                                    label='Name:'
                                    value={business.name}
                                    name='name'
                                    onChange={this.handleChangeBusiness}
                                />
                                <TextInput
                                    s={12}
                                    l={6}
                                    m={6}
                                    xl={6}
                                    label='Address'
                                    name='address'
                                    value={business.address}
                                    onChange={this.handleChangeBusiness}
                                />
                                <TextInput
                                    s={12}
                                    l={6}
                                    m={6}
                                    xl={6}
                                    label='Phone:'
                                    name='phone'
                                    value={business.phone}
                                    onChange={this.handleChangeBusiness}
                                />

                                {this.displayProvince(business.province)}
                            </Row>
                            <Row>
                                <Button node='button' waves='green'>
                                    Update
                                </Button>
                            </Row>
                        </form>

                        <Row>
                            <Col x={12} s={12} l={12} m={12}>
                                <h5>Contacts</h5>
                                <Table>
                                    <thead>
                                        <tr>
                                            <th data-field='id'>Name</th>
                                            <th data-field='name'>Phone</th>
                                            <th data-field='email'>Email</th>
                                            <th data-field='action'>Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {contacts.map(contact => {
                                            return (
                                                <tr>
                                                    <td>{contact.name}</td>
                                                    <td>{contact.phone}</td>
                                                    <td>{contact.email}</td>
                                                    <td>
                                                        <a href="#"><Icon>create</Icon>Edit</a>
                                                        <a href="#"><Icon>clear</Icon>Remove</a>
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </Table>
                            </Col>
                        </Row>
                    </div>
                ) : (
                    <Loading />
                )}
            </Modal>
        );
    }
}

const mapStateToProps = state => {
    return { provinces: state.provinces };
};

const mapDispatchToProps = {};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(BusinessModalDetail);
