import React, { Component } from "react";
import { connect } from "react-redux";
import { Chip, Icon, Row, Select } from "react-materialize";
function mapStateToProps(state) {
  return {};
}

class ContributorForm extends Component {
  displayContributor = () => {
    const { contributors } = this.props;
    return contributors.map(contributor => {
      return (
        <Chip
          key={contributor._id}
          close
          closeIcon={<Icon className="close">close</Icon>}
          options={null}
        >
          {contributor.name}
        </Chip>
      );
    });
  };

  render() {
    console.log(this.props.project);
    return (
      <div>
        <h5>Contributors: </h5>
        <Row>{this.displayContributor()}</Row>
        <Row>
          <Select
            onChange={function noRefCheck() {}}
            options={{
              classes: "",
              dropdownOptions: {
                alignment: "left",
                autoTrigger: true,
                closeOnClick: true,
                constrainWidth: true,
                container: null,
                coverTrigger: true,
                hover: false,
                inDuration: 150,
                onCloseEnd: null,
                onCloseStart: null,
                onOpenEnd: null,
                onOpenStart: null,
                outDuration: 250
              }
            }}
            value=""
          >
            <option disabled value="">
              Choose your option
            </option>
            <option value="1">Option 1</option>
            <option value="2">Option 2</option>
            <option value="3">Option 3</option>
            <option value="3">Option 3</option>
            <option value="3">Option 3</option>
            <option value="3">Option 3</option>
            <option value="3">Option 3</option>
          </Select>
        </Row>
      </div>
    );
  }
}

export default connect(mapStateToProps)(ContributorForm);
