import React, { Component } from "react";
import { connect } from "react-redux";
import {
  Row,
  TextInput,
  Button,
  Textarea,
  DatePicker
} from "react-materialize";
import StatusDropdown from "../common/StatusDropdown";
import CategoryDropdown from "../common/CategoryDropdown";
import API from "../../utils/API/API";
function mapStateToProps(state) {
  return {};
}

class ProjectForm extends Component {
  state = {
    project: {
      business: "",
      name: "",
      description: "",
      price: "",
      googleLink: "",
      startDate: "",
      completedDate: "",
      status: "",
      category: ""
    },
    edited: false,
    successMessage: "",
    errorMessage: ""
  };

  handleChange = (e) => {
    const field = e.target.name;
    let value = e.target.value;


    let project = this.state.project;
    project[field] = value;
    this.setState({
        project,
        edited: true
    });
  }


  handleSubmit = e => {
    e.preventDefautl();
    API.post("/projects", this.state.project).then(response => {
      console.log(response)
    })
  }

  render() {
      const {edited, project} = this.state
      console.log(project)
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <Row className="">
            <TextInput
              s={12}
              l={12}
              m={12}
              xl={12}
              label="Name:"
              name="name"
                value={project.name}
                onChange={this.handleChange}
              required
            />

            <Textarea
              s={12}
              l={12}
              m={12}
              xl={12}
              label="Description"
              required
              name="description"
              value={project.description}
              data-length={200}
            />
            <StatusDropdown  handleChange={this.handleChange}/>
            <CategoryDropdown handleChange={this.handleChange}/>

            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Price:"
              name="price"
              value={project.price}
              //   value={business.phone}
              //   onChange={this.handleChange}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Google Link:"
              name="googleLink"
              value={project.googleLink}
              //   value={business.phone}
              //   onChange={this.handleChange}
              required
            />

            <DatePicker
              name="startDate"
              label="Start Date:"
              s={12}
              l={6}
              m={6}
              xl={6}

            />

            <DatePicker
              name="completedDate"
              label="Completed Date:"
              s={12}
              l={6}
              m={6}
              xl={6}
            />
          </Row>
          <Row>
            <Button
              node="button"
              className="gradient-btn btn-primary"
              waves="green"
              disabled={!edited}
            >
              Save
            </Button>
          </Row>
        </form>
      </div>
    );
  }
}

export default connect(mapStateToProps)(ProjectForm);
