import React, { Component } from "react";
import { Row, Col, TextInput, Button, Icon } from "react-materialize";
import { connect } from "react-redux";
import Loading from "../loading/Loading";
import axios from "axios";
import { update_profile } from "../../redux/actions/index";

const mapStateToProps = state => {
    return { user: state.user };
};

function mapDispatchToProps(dispatch) {
    return {
        update_profile: newProfile => dispatch(update_profile(newProfile))
    };
}

class Profile extends Component {

   constructor(props) {
       super(props)
       this.state = {
        user: {
            name: "",
            email: "",
            phone: ""
        },
        currentPassword:'',
        newPassword:'',
        repeatPassword:'',
        errMessage: "",
        sussess: false
    }
    this.includeTokenWithRequest.bind(this)
   }



    componentDidMount() {
        const user = this.props.user;
        this.setState(prevState => ({
            ...prevState,
            user: {
                ...this.state.user,
                email: user.email,
                name: user.name,
                phone: user.phone
            }
        }));
    }

    handleInputChange = e => {
        const user = { ...this.state.user };
        const field = e.target.name;
        const newValue = e.target.value;
        user[field] = newValue;
        this.setState({ user });
    };

    handlePasswordInputChange = e => {
        const field = e.target.name;
        const newValue = e.target.value;
        this.setState({
            [field]: newValue
        })
    }

    handleSubmitNewPassword = e => {
        e.preventDefault()
        const {currentPassword, newPassword, repeatPassword} = this.state
        const data = {currentPassword, newPassword, repeatPassword}
        this.setState({
            errMessage: "",
            sussess: false
        });
        if(newPassword !== repeatPassword) {
            this.setState({
                errMessage: "New password and repeat password do not match!",
            })
        } 
        else {
            console.log(data)
            //get the token for verifying request
            this.includeTokenWithRequest()
            axios.patch(`user/update/password`, {
                data
            })
        }



    };

    includeTokenWithRequest () {
        const token = localStorage.getItem("token");
        axios.defaults.baseURL = "http://localhost:3000/";
        axios.defaults.headers.common = { Authorization: `Bearer ${token}` };
    }

    handleSubmit = e => {
        e.preventDefault();
        const data = this.state.user;
        this.setState({
            errMessage: "",
            sussess: false
        });

        //get the token for verifying request
        this.includeTokenWithRequest()

        axios
            .patch(`users/update/profile`, {
                data
            })
            .then(response => {
                const user = response.data;
                this.props.update_profile(user);
                this.setState({
                    sussess: true,
                });
            })
            .catch(error => {
                this.setState({ 
                    errMessage: "Some errors has been occurred!"
                })
            });
    };

    render() {
        if (!this.state.user) {
            return <Loading />;
        }
        const { user, errMessage, sussess } = this.state;

        return (
            <div>
                <h5>User Profile</h5>
                {sussess && (
                    <Row>
                        <Col s={12} m={6} l={6} xl={6}>
                            <p className='green-text'>Update Sussessfully </p>
                        </Col>
                    </Row>
                )}

                {errMessage && (
                    <Row>
                        <Col s={12} m={12} l={12} xl={12}>
                            <p className='red-text'>{errMessage || ""}</p>
                        </Col>
                    </Row>
                )}
                <form onSubmit={this.handleSubmit}>
                    <Row>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                s={12}
                                l={12}
                                m={12}
                                label='Name'
                                value={user.name}
                                name='name'
                                onChange={this.handleInputChange}
                            />
                        </Col>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                email
                                validate
                                disabled
                                s={12}
                                l={12}
                                m={12}
                                label='Email'
                                value={user.email}
                                readOnly
                            />
                        </Col>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                s={12}
                                l={12}
                                m={12}
                                label='Phone'
                                name='phone'
                                value={user.phone}
                                onChange={this.handleInputChange}
                            />
                        </Col>
                    </Row>

                    <Row>
                        <Button node='button' type='submit' waves='light'>
                            Save
                            <Icon right>send</Icon>
                        </Button>
                    </Row>
                </form>
                <form onSubmit={this.handleSubmitNewPassword}>
                    <Row>
                        <h5>Change Password</h5>
                        <Col s={12} m={12} l={12} xl={12}>
                            <TextInput
                                password
                                required
                                s={12}
                                l={12}
                                m={12}
                                label='Current Password'
                                name='currentPassword'
                                value={user.currentPassword}
                                onChange={this.handlePasswordInputChange}
                            />
                        </Col>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                password
                                required
                                s={12}
                                l={12}
                                m={12}
                                label='New Password'
                                name='newPassword'
                                value={user.newPassword}
                                onChange={this.handlePasswordInputChange}
                            />
                        </Col>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                password
                                required
                                s={12}
                                l={12}
                                m={12}
                                label='Repeat Password'
                                name='repeatPassword'
                                value={user.repeatPassword}
                                onChange={this.handlePasswordInputChange}
                            />
                        </Col>
                    </Row>

                    <Row>
                        <Button node='button' type='submit' waves='light'>
                            Change Password
                            <Icon right>lock</Icon>
                        </Button>
                    </Row>
                </form>
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Profile);
