import React, { Component } from "react";
import { connect } from "react-redux";
import { TextInput, Button, Row } from "react-materialize";

function mapStateToProps(state) {
  return {};
}

class StatusForm extends Component {

    state={
        status:{}
    }
    componentDidMount(){
        const {status} = this.props
        this.setState({
            status
        })
    }
    handleChange = (e) => {
        const field = e.target.name
        const value = e.target.value
        let {status} = this.state
        status[field] = value
        this.setState({
            status
        })
    }
  render() {
      const {status} = this.state

    return (
      <div>
        <form>
          <Row>
            <TextInput s={12} m={6} xl={6} l={6} label="Title:" name="title" value={status.title} onChange={this.handleChange} />
            <TextInput s={12} m={6} xl={6} l={6} label="Color:" name="color" value={status.color} onChange={this.handleChange} />
          </Row>
          <Row>
              <Button className="gradient-btn btn-green">
                  Save
              </Button>
          </Row>
        </form>
      </div>
    );
  }
}

export default connect(mapStateToProps)(StatusForm);
