import React, { Component } from "react";
import { connect } from "react-redux";
import { Table } from "react-materialize";
import API from "../../utils/API/API";
import Loading from "../loading/Loading";
function mapStateToProps(state) {
  return {};
}

class ProjectTable extends Component {
  state = {
    loading: true,
    projects: []
  };
  componentDidMount() {
    if (this.props.business) {
      const businessId = this.props.business._id;
      this.setState({
        ...this.state,
        loading: true
      });
      API.get(`/projects/${businessId}`)
        .then(response => {
          const projects = response.data;
          this.setState({
            ...this.state,
            loading: false,
            projects
          });
        })
        .catch(error => {
          console.log(error);
          this.setState({
            ...this.state,
            loading: false
          });
        });
    }
  }

  displayProjects = projects => {
    projects.map(project => {
      return (
        <tr key={project._id}>
          <td>
            <a href="#">{project.name}</a>
          </td>
          <td>{project.price}</td>
          <td>{project.status}</td>
          <td>{project.startDate}</td>
          <td>{project.completedDate}</td>
        </tr>
      );
    });
  };

  render() {
    const { loading, projects } = this.state;
    if (loading) {
      return <Loading />;
    }
    return (
      <Table>
        <thead>
          <tr>
            <th data-field="id">Project Name</th>
            <th data-field="name">Price</th>
            <th data-field="price">Status</th>
            <th data-field="price">Start Date</th>
            <th data-field="price">Completed Date</th>
          </tr>
        </thead>
        <tbody>
          {projects.map(project => {
            return (
              <tr key={project._id}>
                <td>
                  <a href="#">{project.name}</a>
                </td>
                <td>{project.price}</td>
                <td>{project.status}</td>
                <td>{project.startDate}</td>
                <td>{project.completedDate}</td>
              </tr>
            );
          })}
          <tr>
            <td>
              <a href="#">Alvin</a>
            </td>
            <td>200.00</td>
            <td>Completed</td>
            <td>Jan 15, 2020</td>
            <td>Feb 15, 2020</td>
          </tr>
          <tr>
            <td>
              <a href="#">Restaurant Web</a>
            </td>
            <td>200.00</td>
            <td>Completed</td>
            <td>Jan 15, 2020</td>
            <td>Feb 15, 2020</td>
          </tr>
        </tbody>
      </Table>
    );
  }
}

export default connect(mapStateToProps)(ProjectTable);
