import React, { Component } from "react";
import { connect } from "react-redux";
import { Chip, Icon, Row, Dropdown, Divider, Button } from "react-materialize";
function mapStateToProps(state) {
  return {};
}

class ContributorForm extends Component {
  displayContributor = () => {
    const { contributors } = this.props;
    return contributors.map(contributor => {
      return (
        <Chip
          key={contributor._id}
          close
          closeIcon={<Icon className="close">close</Icon>}
          options={null}
        >
          {contributor.name}
        </Chip>
      );
    });
  };

  render() {
    console.log(this.props.project);
    return (
      <div>
        <h5>Contributors: </h5>
        <Row>{this.displayContributor()}</Row>
        <Row>
          <Dropdown
            options={{
              alignment: "left",
              autoTrigger: true,
              closeOnClick: true,
              constrainWidth: true,
              container: null,
              coverTrigger: true,
              hover: false,
              inDuration: 150,
              onCloseEnd: null,
              onCloseStart: null,
              onOpenEnd: null,
              onOpenStart: null,
              outDuration: 250
            }}
            trigger={<Button node="button">Drop Me!</Button>}
          >
            <a href="#">one</a>
            <a href="#">two</a>
            <Divider />
            <a href="#">three</a>
            <a href="#">
              <Icon>view_module</Icon>
              four
            </a>
            <a href="#">
              <Icon>cloud</Icon> five
            </a>
          </Dropdown>
        </Row>
      </div>
    );
  }
}

export default connect(mapStateToProps)(ContributorForm);
