import React, { Component } from "react";
import { connect } from "react-redux";
import {
    Modal,
    Button,
    Row,
    Col,
    TextInput,
    Select,
    Table, Icon
} from "react-materialize";
import Loading from "../loading/Loading";
import axios from "axios";
import ContactForm from "../contact/ContactForm";
import { EEXIST } from "constants";

class BusinessModalDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
        };
    }

    componentDidMount() {
        this.setState({
            business: this.props.business
        });
        this.getBusinessDetail(this.props.business._id);
    }

    componentDidUpdate(prevProps) {
        if(prevProps.business._id !== this.props.business._id){
            this.getBusinessDetail(this.props.business._id)
        }
    }

    getBusinessDetail(id) {
        this.setState({
            ...this.state,
            loading: true
        });
        axios
            .get(`businesses/${id}`)
            .then(response => {
                if (response.data.businesses) {
                    this.setState({
                        ...this.state,
                        contacts: response.data.businesses.contacts,
                        province: response.data.businesses.province,
                        loading: false
                    });
                }
            })
            .catch(err => {
                this.setState({
                    loading: false
                });
            });
    }

    handleChangeBusiness = e => {
        const field = e.target.name
        const value = e.target.value 

        let business = this.state.business
        business[field] = value
        this.setState({
            ...this.state,
            business
        })
    };

    updateBusiness = e => {
        e.preventDefault()
        const _id = this.state.business._id
        const business = {
            name: this.state.business.name,
            phone: this.state.business.phone,
            address: this.state.business.address,
            province: this.state.business.province
        }
        axios.patch(`businesses/${_id}`, business).then(response => {
            console.log(response.data)
        }).catch(error => {
            console.log(error)
        })
    }

    displayProvince(_id) {
        const { provinces } = this.props;
        const province = provinces.filter(province => province._id === _id)[0];
        return (
            <Select
                options={{
                    classes: "",
                    dropdownOptions: {
                        alignment: "left",
                        autoTrigger: true,
                        closeOnClick: true,
                        constrainWidth: true,
                        container: null,
                        coverTrigger: true,
                        hover: false,
                        inDuration: 150,
                        onCloseEnd: null,
                        onCloseStart: null,
                        onOpenEnd: null,
                        onOpenStart: null,
                        outDuration: 250
                    }
                }}
                onChange={this.handleChangeBusiness}
                value={province._id}
                name='province'
            >
                <option disabled value=''>
                    Select Province
                </option>
                {provinces.map(province => {
                    return (
                        <option
                            className='black-text'
                            key={province._id}
                            value={province._id}
                        >
                            {province.name}
                        </option>
                    );
                })}
            </Select>
        );
    }

    render() {

        const contacts = this.state.contacts || [];
        if(!this.state.business) {
            return <Loading/>
        }
        const { business } = this.state;
        return (
            <Modal
                actions={[
                    <Button flat modal='close' node='button' waves='green'>
                        Close
                    </Button>
                ]}
                open={true}
                style={{ boder: "none" }}
                bottomSheet={false}
                fixedFooter={false}
                header='Business Detail'
                id='modal1'
                required
                options={{
                    dismissible: true,
                    endingTop: "10%",
                    inDuration: 250,
                    onCloseEnd: null,
                    onCloseStart: null,
                    onOpenEnd: null,
                    onOpenStart: null,
                    opacity: 0.5,
                    outDuration: 250,
                    preventScrolling: true,
                    startingTop: "4%"
                }}
            >
                {!this.state.loading ? (
                    <div>
                        <form onSubmit={this.updateBusiness}>
                            <Row>
                                <TextInput
                                    s={12}
                                    l={6}
                                    m={6}
                                    xl={6}
                                    label='Name:'
                                    value={business.name}
                                    name='name'
                                    onChange={this.handleChangeBusiness}
                                    required
                                />
                                <TextInput
                                    s={12}
                                    l={6}
                                    m={6}
                                    xl={6}
                                    label='Address'
                                    name='address'
                                    value={business.address}
                                    onChange={this.handleChangeBusiness}
                                    required
                                />
                                <TextInput
                                    s={12}
                                    l={6}
                                    m={6}
                                    xl={6}
                                    label='Phone:'
                                    name='phone'
                                    value={business.phone}
                                    onChange={this.handleChangeBusiness}
                                    required
                                />

                                {this.displayProvince(business.province)}
                            </Row>
                            <Row>
                                <Button node='button' waves='green'>
                                    Update
                                </Button>
                            </Row>
                        </form>

                        <Row>
                            <Col x={12} s={12} l={12} m={12} xl={12}>
                                <h5>Contacts</h5>
                                <Table x={12} s={12} l={12} m={12} xl={12}>
                                    <thead>
                                        <tr>
                                            <th data-field='id'>Name</th>
                                            <th data-field='name'>Phone</th>
                                            <th data-field='email'>Email</th>
                                            <th data-field='action'>Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {contacts.map(contact => {
                                            return (
                                                <tr key={contact._id}>
                                                    <td>{contact.name}</td>
                                                    <td>{contact.phone}</td>
                                                    <td>{contact.email}</td>
                                                    <td>
                                                        <a href="#"><Icon>create</Icon></a>
                                                        <a href="#" style={{marginLeft:"20px", color: "red"}}><Icon>clear</Icon></a>
                                                    </td>
                                                </tr>
                                            );
                                        })}
                                    </tbody>
                                </Table>
                            </Col>
                        </Row>

                        <Row>
                            <Col s={12} l={12} m={12} xl={12}>
                                        <ContactForm/>
                            </Col>
                        </Row>
                    </div>
                ) : (
                    <Loading />
                )}
            </Modal>
        );
    }
}

const mapStateToProps = state => {
    return { provinces: state.provinces };
};

const mapDispatchToProps = {};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(BusinessModalDetail);
