import React, { Component } from "react";
import { connect } from "react-redux";
import { Row, TextInput } from "react-materialize";
import Button from "react-materialize/lib/Button";
import StatusDropdown from "../common/StatusDropdown";
function mapStateToProps(state) {
  return {};
}

class ProjectFilter extends Component {
  render() {
    return (
      <div>
        <form>
          <Row
            className=" radius-corner"
            style={{
              backgroundColor: "white",
              padding: "20px",
              boxShadow: "1px 1px 1px #9E9E9E"
            }}
          >
            <h6>Filter by</h6>
            <TextInput label="Project Name:" s={6} m={3} xl={3} l={3} />
            <StatusDropdown/>
            <TextInput label="Category:" s={6} m={3} xl={3} l={3} />
            <Button
              type="submit"
              className="gradient-btn btn-blue"
              node="button"
              style={{
                marginRight: "5px",
                bottom: "-20px",
                marginBottom: "20px"
              }}
            >
              Search
            </Button>
            <Button
              className="gradient-btn btn-white"
              node="button"
              style={{
                marginRight: "5px",
                bottom: "-20px",
                marginBottom: "20px"
              }}
            >
              Reset
            </Button>
          </Row>
        </form>
      </div>
    );
  }
}

export default connect(mapStateToProps)(ProjectFilter);
