import React, { Component } from "react";
import { connect } from "react-redux";
import { Chip, Icon, Row, Table, Button } from "react-materialize";
import API from "../../../utils/API/API";
import Loading from "../../loading/Loading";

function mapStateToProps(state) {
  return {};
}

class ContributorForm extends Component {
  state = {
    contributors: [],
    users: [],
    project: {}
  };
  componentDidMount() {
    let { contributors, users, project } = this.props;

    this.setState({
      contributors,
      users,
      project
    });
  }
//   componentWillReceiveProps(nextProps) {
//     if(nextProps.contributors !== this.props.contributor){
//         this.setState({
//             contributors: nextProps.contributors,
//             users:nextProps.users,
//             project: nextProps.project
//         })
//     }
//   }
  displayContributor = () => {
    const { contributors } = this.props;
    console.log(contributors)
    const data = [];
    contributors.forEach(contributor => {
      const name = contributor.name;
      data.push({ tag: name });
    });
    return (
      <Chip
        close={true}
        closeIcon={<Icon className="close">close</Icon>}
        options={{
          data: data,
          placeholder: "Contributors",
        }}
      />
    );
  };
  removeContributor = contributor => () => {
    console.log(contributor);
  };

  addContributor = user => () => {
    const userId = user._id;
    const projectId = this.props.project._id;
  };

  displayUser = () => {
    const { users } = this.props;
    return users.map(user => {
      return (
        <tr key={user._id}>
          <td>{user.name}</td>
          <td>{user.email}</td>
          <td>{user.role}</td>
          <td>
            {user.active.toString() === "true" ? (
              <span className="dot active"></span>
            ) : (
              <span className="dot inactive"></span>
            )}
          </td>
          <td>
            <Button
              node="button"
              className="gradient-btn btn-red"
              style={{
                marginRight: "5px"
              }}
              waves="light"
              onClick={this.addContributor(user)}
            >
              Add
              <Icon left>add</Icon>
            </Button>
          </td>
        </tr>
      );
    });
  };

  render() {
    const { contributors } = this.props;
    if(!contributors){
        return <Loading/>
    }

    const displayContributors = this.displayContributor()

    return (
      <div>
        {contributors.length > 0 && displayContributors }
        <Row>
          <Table hoverable={true} responsive={true}>
            <thead>
              <tr>
                <th data-field="name">Name</th>
                <th data-field="email">Email</th>
                <th data-field="role">Role</th>
                <th data-field="active">Active</th>
                <th data-field="add">Add</th>
              </tr>
            </thead>
            <tbody>{this.displayUser()}</tbody>
          </Table>
        </Row>
        <Row>
          <Button
            node="button"
            style={{
              margin: "5px"
            }}
            waves="light"
            className="gradient-btn btn-green"
          >
            Save
            <Icon left>save</Icon>
          </Button>
        </Row>
      </div>
    );
  }
}

export default connect(mapStateToProps)(ContributorForm);
