import React, { Component } from "react";
import { connect } from "react-redux";
import { TextInput, Row, Select } from "react-materialize";

function mapStateToProps(state) {
  return {};
}

class UserForm extends Component {
  render() {
    return (
      <div>
        <Row>
          <TextInput label="Name" s={12} m={6} xl={6} l={6} />
          <TextInput label="Email" validate email s={12} m={6} xl={6} l={6} />
          <TextInput label="Phone" s={12} m={6} xl={6} l={6} />
          <Select
            label="Role"
            options={{
              classes: "",
              dropdownOptions: {
                alignment: "left",
                autoTrigger: true,
                closeOnClick: true,
                constrainWidth: true,
                container: null,
                coverTrigger: true,
                hover: false,
                inDuration: 150,
                onCloseEnd: null,
                onCloseStart: null,
                onOpenEnd: null,
                onOpenStart: null,
                outDuration: 250
              }
            }}
            value="user"
          >
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </Select>
        </Row>
      </div>
    );
  }
}

export default connect(mapStateToProps)(UserForm);
