import React, { Component } from "react";
import { connect } from "react-redux";
import API from "../../utils/API/API";
import Loading from "../loading/Loading";
import { Row } from "react-materialize";
import BusinessForm from "./BusinessForm";
function mapStateToProps(state, ownProps) {
    const businessId = ownProps.match.params.id
    let business = {}
    if(businessId && state.businesses.length > 0) {
        business = state.businesses.filter(business => business.id === businessId)[0]
    }
    console.log(state.businesses)
  return {
      business
  };
}

class BusinessDetailPage extends Component {
  state = {
    loading: true,
    errorMessage: "",
    business: {}
  };
  componentDidMount() {
    if(this.props.business) {
        this.setState({
            business: this.props.business,
            loading: false,
            errorMessage: "",
        })
    }
    // const businessId = this.props.match.params.id;
    // API.get(`businesses/${businessId}`)
    //   .then(response => {
    //     this.setState({
    //       ...this.state,
    //       business: response.data.business,
    //       loading: false,
    //       errorMessage: ""
    //     });
    //   })
    //   .catch(error => {
    //     this.setState({
    //       business: {},
    //       loading: false,
    //       errorMessage: "Cannot fetch the business"
    //     });
    //   });
  }
  render() {
    const { business, errorMessage, loading } = this.state;
    if (loading) {
      return <Loading />;
    }

    return (
      <div>
        <Row style={{ marginTop: "20px" }}></Row>
        <Row><h5>{business.name}</h5></Row>
        <Row
          className=" radius-corner"
          style={{
            backgroundColor: "white",
            padding: "20px",
            boxShadow: "1px 1px 1px #9E9E9E"
          }}
        >
            <BusinessForm business={business}/>
        </Row>
      </div>
    );
  }
}

export default connect(mapStateToProps)(BusinessDetailPage);
