import React, { Component } from "react";
import { connect } from "react-redux";
import { Table, Icon } from "react-materialize";
import API from "../../../utils/API/API"
function mapStateToProps(state) {
  return {};
}

class ProjectTableAdmin extends Component {
    state = {
        loading: true,
        projects: [],
        statuses: []
      };
  componentDidMount() {
    if (this.props.projects) {
      this.setState({
        ...this.state,
        projects: this.props.projects,
        loading: false
      });
    }

    API.get("/statuses")
      .then(response => {
        this.setState({
          ...this.state,
          statuses: response.data,
          loading: false
        });
      })
      .catch(error => {
        this.setState({
          ...this.state,
          loading: false
        });
        console.log(error);
      });
  }
  displayProjects = projects => {
    const { statuses } = this.state;
    return projects.map(project => {
      const startDate = new Date(project.startDate);
      const completedDate = new Date(project.completedDate);
      const status = statuses.filter(
        status => status._id === project.status
      )[0];
      return (
        <tr key={project._id}>
          <td>
            <a href={"/projects/" + project._id}>{project.name}</a>
          </td>
          <td>{project.price.$numberDecimal}</td>
          <td>
            {status && (
              <p style={{ color: `${status.color}` }}>{status.title}</p>
            )}
          </td>
          <td>{project.startDate && startDate.toDateString()}</td>
          <td>{project.completedDate && completedDate.toDateString()}</td>
        </tr>
      );
    });
  };
  render() {
    const { projects } = this.props;
    return (
      <div>
        <Table>
          <thead>
            <tr>
              <th data-field="name">Name</th>
              <th data-field="price">Price</th>
              <th data-field="status">Status</th>
              <th data-field="price">Contributors</th>
              <th data-field="price">Edit</th>
            </tr>
          </thead>
          <tbody>
          {projects.length>0 ? (
            this.displayProjects(projects)
          ) : (
            <tr>
              <td>There is no project found!</td>
            </tr>)}
          </tbody>
        </Table>
      </div>
    );
  }
}

export default connect(mapStateToProps)(ProjectTableAdmin);
