import React, { Component } from "react";
import { connect } from "react-redux";
import { TextInput, Row, Select } from "react-materialize";
import phoneFormat from "../../../utils/commons/FormatPhone";

function mapStateToProps(state) {
  return {};
}

class UserForm extends Component {
    handleChange = (e) => {
        const field = e.target.name
        let value = e.target.value
        if(field === "phone"){
            value = phoneFormat(value)
        }
    }
  render() {
    return (
      <div>
        <Row>
          <TextInput label="Name"  name="name" s={12} m={6} xl={6} l={6} />
          <TextInput label="Email"  name="email" validate email s={12} m={6} xl={6} l={6} />
          <TextInput label="Phone" s={12} m={6} xl={6} l={6}  name="phone" />
          <Select
            s={12}
            m={6}
            xl={6}
            l={6}
            label="Role"
            name="role"
            options={{
              classes: "",
              dropdownOptions: {
                alignment: "left",
                autoTrigger: true,
                closeOnClick: true,
                constrainWidth: true,
                container: null,
                coverTrigger: true,
                hover: false,
                inDuration: 150,
                onCloseEnd: null,
                onCloseStart: null,
                onOpenEnd: null,
                onOpenStart: null,
                outDuration: 250
              }
            }}
          >
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </Select>
        </Row>
      </div>
    );
  }
}

export default connect(mapStateToProps)(UserForm);
