import React, { Component } from "react";
import { connect } from "react-redux";
import API from "../../utils/API/API";
import Loading from "../loading/Loading";
import { Row, Button, Icon } from "react-materialize";
import { Redirect, Link } from "react-router-dom";
import CreateProjectModal from "../projects/CreateProjectModal";
import ProjectTable from "../projects/ProjectTable";

//Gets all businesses from redux
//if there is no businesses in redux then redirect to businesses page and reload them
function mapStateToProps(state, ownProps) {
  const businessId = ownProps.match.params.id;
  let business = {};

  if (!state.businesses) {
    ownProps.history.push("/businesses");
    return {};
  }

  if (businessId && state.businesses.length > 0) {
    business = state.businesses.filter(
      business => business._id === businessId
    )[0];
  }

  return {
    business
  };
}

class BusinessDetailPage extends Component {
  state = {
    loading: true,
    errorMessage: "",
    business: {},
    openCreateProjectModal: false,
    projects: []
  };

  getProjectsData = () => {
    const businessId = this.props.business._id;
    API.get(`/projects/business/${businessId}`)
      .then(response => {
        const projects = response.data;
        this.setState({
          ...this.state,
          projects
        });
      })
      .catch(error => {
        console.log(error);
        this.setState({
          ...this.state,
          loading: false
        });
      });
  };

  updateData = () => {};

  getBusinessData = () => {
    const businessId = this.props.match.params.id;
    API.get(`/businesses/${businessId}`).then(response => {
      const projects = response.data.projects;
      this.setState({
        ...this.state,
        projects,
        business: response.data,
        loading: false
      });
    })
  }

  componentDidMount() {
    if (this.props.business) {
      this.getProjectsData();
      this.getBusinessData()
      this.setState({
        ...this.state,
        business: this.props.business,
        loading: false,
        errorMessage: ""
      });
    }
  }


  handleOpenCreateProjectModel = () => {
    this.setState({
      ...this.state,
      openCreateProjectModal: true
    });
  };

  closeCreateProjectModal = () => {
    this.setState({
      ...this.state,
      openCreateProjectModal: false
    });
  };

  handleGoBack = () => {
    this.props.history.push('/businesses')
  }
  render() {
    const { loading } = this.state;

    if (loading) {
      return <Loading />;
    }
    const { business, openCreateProjectModal, projects } = this.state;

    return (
      <div>
        <Row style={{ marginTop: "20px" }}>
          <Button node="button" type="submit" waves="light" className="gradient-btn btn-white" onClick={this.handleGoBack}>
            Go Back
            <Icon left>arrow_back</Icon>
          </Button>
        </Row>
        <Row>
          <h5>
            {business.name}{" "}
            <Button
              node="button"
              type="submit"
              waves="light"
              className="gradient-btn btn-red"
              onClick={this.handleOpenCreateProjectModel}
            >
              Create New Project
              <Icon left>add_box</Icon>
            </Button>
          </h5>
        </Row>

        <Row
          className=" radius-corner"
          style={{
            backgroundColor: "white",
            padding: "20px",
            boxShadow: "1px 1px 1px #9E9E9E"
          }}
        >
          {projects && <ProjectTable business={business} projects={projects} />}
        </Row>

        {openCreateProjectModal && (
          <CreateProjectModal
            closeCreateProjectModal={this.closeCreateProjectModal}
            business={business}
            getProjectsData={this.getProjectsData}
          />
        )}
      </div>
    );
  }
}

export default connect(mapStateToProps)(BusinessDetailPage);
