import axios from "axios"

let authToken = ""

if(localStorage.getItem("token")){
    authToken = localStorage.getItem("token");
}

const API = axios.create({
    baseURL : "http://localhost:3000/"
})

API.defaults.headers.common = { Authorization: `Bearer ${authToken}` }

export default API
