import React, { Component } from "react";
import { connect } from "react-redux";
import { Row, Button, Icon } from "react-materialize";
import API from "../../../utils/API/API";
import ProvinceModal from "./ProvinceModal";

function mapStateToProps(state) {
  return {};
}

class Province extends Component {
  state = {
    provinces: [],
    loading: true,
    openCreateModal: false
  };
  componentDidMount() {
    this.getProvinces();
  }

  getProvinces = () => {
    API.get(`/provinces`).then(response => {
      const provinces = response.data;
      this.setState({
        loading: false,
        provinces
      });
    });
  };
  handleCreateModel = () => {
    this.setState({
      ...this.state,
      openCreateModal: true
    });
  };

  closeCreateModal = () => {
    this.setState({
      openCreateModal: false
    });
  };

  render() {
    return (
      <div>
        <Row style={{ marginTop: "20px" }}></Row>{" "}
        <h5>
          Provinces{" "}
          <Button
            node="button"
            type="submit"
            waves="light"
            className="gradient-btn btn-red"
            onClick={this.handleCreateModel}
          >
            Add
            <Icon left>add_box</Icon>
          </Button>
        </h5>
        <Row
          className=" radius-corner"
          style={{
            backgroundColor: "white",
            padding: "20px",
            boxShadow: "1px 1px 1px #9E9E9E"
          }}
        >
          {/* <StatusTable statuses={statuses} /> */}
        </Row>
        {openCreateModal && (
          <ProvinceModal
            closeModal={this.closeCreateModal}
            updateData={this.getStatuses}
          />
        )}
      </div>
    );
  }
}

export default connect(mapStateToProps)(Province);
