import React, { Component } from "react";
import { connect } from "react-redux";
import { TextInput, Icon, Button, Row } from "react-materialize";

const phoneRegex = /^\(?([0-9]{0,3})\)?[-. ]?([0-9]{0,3})[-. ]?([0-9]{0,4})$/

const handleInput = (value) => {
    return (
      value.replace(phoneRegex, '($1) $2-$3')
    )
  }

class ContactForm extends Component {

    constructor(props){
        super(props)
        this.state = {
            contact: {
                name: '',
                phone: '',
                email: ''
            }
        }
    }

    handleInputChange = (e) => {

        const field = e.target.name 
        let value = e.target.value 

        if(field === 'phone'){
            value = handleInput(value)
        }

        let contact = {
            ...this.state.contact,
            [field]: value
        }

        this.setState({
            contact
        })

    }

    render() {
        const contact = this.state.contact 

        return (
            <div>
                <form>
                    <Row>
                        <TextInput
                            s={12}
                            l={2}
                            m={2}
                            xl={2}
                            label='Name'
                            name='name'
                            value={contact.name}
                            required
                            onChange={this.handleInputChange}
                        />
                        <TextInput
                            s={12}
                            l={3}
                            m={3}
                            xl={3}
                            label='Phone'
                            name='phone'
                            value={contact.phone}
                            required
                            onChange={this.handleInputChange}
                        />
                        <TextInput
                            s={12}
                            l={6}
                            m={4}
                            xl={4}
                            label='Email'
                            email
                            validate
                            value={contact.email}
                            name='email'
                            onChange={this.handleInputChange}
                        />
                        <Button
                            node='button'
                            type='submit'
                            waves='light'
                            style={{marginTop: '20px'}}
                        >
                            Save
                            <Icon left>save</Icon>
                        </Button>
                    </Row>
                </form>
            </div>
        );
    }
}

const mapStateToProps = state => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(ContactForm);
