import React, { Component } from "react";
import { connect } from "react-redux";
import { Modal, Button } from "react-materialize";
import API from "../../../utils/API/API";
import Loading from "../../loading/Loading";
function mapStateToProps(state) {
  return {};
}

class EditContributorsModal extends Component {
  state = {
    users: []
  };
  componentDidMount() {
    const { project } = this.props;
    this.getUsers();
  }

  displayContributors = users => {
    const { project } = this.props;
    console.log(project.contributors)
    const contributors = users.filter(user => {
      return users.includes(project.contributors);
    });
    console.log(contributors);
  };

  getUsers = () => {
    API.get(`/admin/users`)
      .then(response => {
        this.setState({
          users: response.data,
          loading: false
        });
      })
      .catch(error => {
        this.setState({
          loading: true
        });
        this.props.history.push("/notfound");
      });
  };
  render() {
    const { project } = this.props;
    const { users, loading } = this.state;
    if(loading){
        return <Loading/>
    }
    this.displayContributors(users);

    return (
      <div>
        <Modal
          actions={[
            <Button flat modal="close" node="button" waves="green">
              Close
            </Button>
          ]}
          bottomSheet={false}
          fixedFooter={false}
          header="Edit Contributors"
          id="modal-0"
          open={true}
          options={{
            dismissible: true,
            endingTop: "10%",
            inDuration: 250,
            onCloseEnd: this.props.closeEditContributorsModal,
            onCloseStart: null,
            onOpenEnd: null,
            onOpenStart: null,
            opacity: 0.5,
            outDuration: 250,
            preventScrolling: true,
            startingTop: "4%"
          }}
        >
          <p>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat. Duis aute irure dolor in
            reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
            pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
            culpa qui officia deserunt mollit anim id est laborum
          </p>
        </Modal>
      </div>
    );
  }
}

export default connect(mapStateToProps)(EditContributorsModal);
