import React, { Component } from "react";
import { connect } from "react-redux";
import { Col, Row, Table, TextInput, Select } from "react-materialize";
import Button from "react-materialize/lib/Button";

export class Business extends Component {

    state = {
        searchBy: {
            name: '',
            phone:'',
            province: ''
        }
    }

    handleSubmit = e => {
        e.preventDefault();
    };

    handleInputChange = e => {
        const searchBy = {...this.state.searchBy}
        const field = e.target.name 
        const value = e.target.value 
        searchBy[field] = value
        this.setState({
            ...this.state,
            searchBy
        })
    }

    render() {
        const {name, phone, province} = this.state.searchBy
        return (
            <div>
                <h3>All Businesses</h3>
                <Row>
                    <form>
                        <Col s={12} m={12} l={12} xl={12}>
                            <h5>Filter by</h5>
                            <TextInput
                                s={12}
                                l={4}
                                m={4}
                                xl={4}
                                label='Name'
                                name='name'
                                value= {name}
                                onChange = {this.handleInputChange}
                            />
                            <TextInput
                                s={12}
                                l={4}
                                m={4}
                                xl={4}
                                label='Phone'
                                name='phone'
                                value= {phone}
                                onChange = {this.handleInputChange}
                            />
                            <Select
                                onChange={ this.handleInputChange}
                                options={{
                                    classes: "",
                                    dropdownOptions: {
                                        alignment: "left",
                                        autoTrigger: true,
                                        closeOnClick: true,
                                        constrainWidth: true,
                                        container: null,
                                        coverTrigger: true,
                                        hover: false,
                                        inDuration: 150,
                                        onCloseEnd: null,
                                        onCloseStart: null,
                                        onOpenEnd: null,
                                        onOpenStart: null,
                                        outDuration: 250
                                    }
                                }}
                                value={province}
                                name='province'
                            >
                                <option disabled value=''>
                                    Select Province
                                </option>
                                <option value='1'>Option 1</option>
                                <option value='2'>Option 2</option>
                                <option value='3'>Option 3</option>
                            </Select>
                            <Button
                                node='button'
                                type="submit"
                                style={{
                                    marginRight: "5px",
                                    marginBottom: "0px"
                                }}
                                waves='light'
                            >
                                Search
                            </Button>
                        </Col>
                    </form>
                    <Col s={12} m={12} l={12} xl={12}>
                        <Table>
                            <thead>
                                <tr>
                                    <th data-field='id'>Name</th>
                                    <th data-field='price'>Phone</th>
                                    <th data-field='price'>Province</th>
                                    <th data-field='price'>Details</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>Alvin</td>
                                    <td>$0.87</td>
                                    <td>Eclair</td>
                                    <td>$0.87</td>
                                </tr>
                                <tr>
                                    <td>Alan</td>
                                    <td>$3.76</td>
                                    <td>Eclair</td>
                                    <td>$0.87</td>
                                </tr>
                                <tr>
                                    <td>Jonathan</td>
                                    <td>$7.00</td>
                                    <td>Eclair</td>
                                    <td>$0.87</td>
                                </tr>
                            </tbody>
                        </Table>
                    </Col>
                </Row>
            </div>
        );
    }
}

const mapStateToProps = state => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(Business);
