import React, { Component } from "react";
import { connect } from "react-redux";
import { TextInput, Row, Select } from "react-materialize";
import phoneFormat from "../../../utils/commons/FormatPhone";

function mapStateToProps(state) {
  return {};
}

class UserForm extends Component {
  state = {
    user: {
      name: "",
      phone: "",
      email: "",
      role: ""
    }
  };
  handleChange = e => {
    const field = e.target.name;
    let value = e.target.value;
    if (field === "phone") {
      value = phoneFormat(value);
    }
    let user = this.state.user;
    user[field] = value;
    this.setState({
      user
    });
  };
  render() {
    const { user } = this.state;
    return (
      <div>
        <form>
          <Row>
            <TextInput
              label="Name"
              name="name"
              s={12}
              m={6}
              xl={6}
              l={6}
              onChange={this.handleChange}
              value={user.name}
            />
            <TextInput
              label="Email"
              name="email"
              validate
              email
              s={12}
              m={6}
              xl={6}
              l={6}
              onChange={this.handleChange}
              value={user.email}
            />
            <TextInput
              label="Phone"
              s={12}
              m={6}
              xl={6}
              l={6}
              name="phone"
              onChange={this.handleChange}
              value={user.phone}
            />
            <Select
              s={12}
              m={6}
              xl={6}
              l={6}
              label="Role"
              name="role"
              onChange={this.handleChange}
              options={{
                classes: "",
                dropdownOptions: {
                  alignment: "left",
                  autoTrigger: true,
                  closeOnClick: true,
                  constrainWidth: true,
                  container: null,
                  coverTrigger: true,
                  hover: false,
                  inDuration: 150,
                  onCloseEnd: null,
                  onCloseStart: null,
                  onOpenEnd: null,
                  onOpenStart: null,
                  outDuration: 250
                }
              }}
              value={user.role}
            >
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </Select>
          </Row>
        </form>
      </div>
    );
  }
}

export default connect(mapStateToProps)(UserForm);
