import React, { Component } from "react";
import { connect } from "react-redux";
import API from "../../utils/API/API";
import {Select} from 'react-materialize'
import Loading from "../loading/Loading";

function mapStateToProps(state) {
  return {};
}

class StatusDropdown extends Component {
    state = {
        loading: true
    }
  componentDidMount() {
    API.get("/statuses")
      .then(response => {
       this.setState({
           ...this.state,
           statuses: response.data,
           loading: false
       })
      })
      .catch(error => {
          this.setState({
            ...this.state,
            loading: false
          })
        console.log(error);
      });
  }

  render() {
    const {loading, statuses} = this.state
    if(loading){
        return <Loading/>
    }

    return (
      <div>
        <Select
          onChange={function noRefCheck() {}}
          options={{
            classes: "",
            dropdownOptions: {
              alignment: "left",
              autoTrigger: true,
              closeOnClick: true,
              constrainWidth: true,
              container: null,
              coverTrigger: true,
              hover: false,
              inDuration: 150,
              onCloseEnd: null,
              onCloseStart: null,
              onOpenEnd: null,
              onOpenStart: null,
              outDuration: 250
            }
          }}
          value=""
        >
          <option disabled value="">
            Choose status
          </option>
         {statuses.map(status => {
             return <option value={status._id} key={status._id}>{status.title}</option>
         })}

        </Select>
      </div>
    );
  }
}

export default connect(mapStateToProps)(StatusDropdown);
