import React, { Component } from 'react';
import { connect } from 'react-redux';
import API from '../../utils/API/API';

function mapStateToProps(state) {
    return {

    };
}

class DisplayStatus extends Component {
    componentDidMount() {
        API.get("/statuses")
        .then(response => {
          this.setState({
            ...this.state,
            statuses: response.data,
            loading: false
          });
        })
        .catch(error => {
          this.setState({
            ...this.state,
            loading: false
          });
          console.log(error);
        });
    }
    displayStatus = (status) => {
        const statuses = this.state.statuses.filter(status =>  status._id === status)
        console.log(statuses)
    }
    render() {
        const {status} = this.props
        return (
            <div>
                {this.displayStatus(this.props.status)}
            </div>
        );
    }
}

export default connect(
    mapStateToProps,
)(DisplayStatus);
