import React, { Component } from "react";
import { SideNavItem } from "react-materialize";
import { connect } from "react-redux";
import API from "../../utils/API/API"
import {logout } from "../../redux/actions/index";

const mapStateToProps = state => {
    return { user: state.user };
};

function mapDispatchToProps(dispatch) {
    return {
        logout: () => dispatch(logout())
    };
}

class SideNavItems extends Component {
    handleLogout = e => {
        e.preventDefault()
        API.post(`users/logout`)
            .then(response => {
                console.log(response)
                if (response) {

                    API.defaults.baseURL = "";
                    API.defaults.headers.common = { Authorization: `` };
                    localStorage.clear();
                    // return Object.assign({}, state, {
                    //     user: {
                    //         authenticated: false
                    //     }
                    // });
                }
            })
            .catch(error => {
                console.log(error);
            });
        this.props.logout()
    };

    render() {
        const { user } = this.props;

        return (
            <div>
                {user.authenticated ? (
                    <div>
                        <SideNavItem
                            user={{
                                background:
                                    "../img/jeremy-bishop-BuQ-jgeexaQ-unsplash.jpg",
                                email: user.email,
                                name: user.name
                            }}
                            userView
                        />
                        <SideNavItem
                            href='/projects'
                            icon='assignment_turned_in'

                        >
                            Projects
                        </SideNavItem>
                        <SideNavItem href='/businesses' icon='business'>
                            Business
                        </SideNavItem>
                        <SideNavItem href='/contacts' icon='account_box'>
                            Contacts
                        </SideNavItem>
                        <SideNavItem divider />
                        <SideNavItem subheader>My Account</SideNavItem>
                        <SideNavItem href='/profile/me' waves icon='person_pin'>
                            Profile
                        </SideNavItem>
                        <SideNavItem
                            href='/logout'
                            onClick={this.handleLogout}
                            waves
                            icon='power_settings_new'
                        >
                            Logout
                        </SideNavItem>
                    </div>
                ) : (
                    <div>
                        <SideNavItem
                            user={{
                                background:
                                    "img/jeremy-bishop-BuQ-jgeexaQ-unsplash.jpg",
                                email: "Beeblee",
                                name: ""
                            }}
                            userView
                        />
                        <SideNavItem href='/login' waves icon='trending_flat'>
                            Login
                        </SideNavItem>
                    </div>
                )}
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SideNavItems);
