import React, { Component } from "react";
import { connect } from "react-redux";
import { Row, TextInput, Button, Textarea } from "react-materialize";
import StatusDropdown from "../common/StatusDropdown";
import CategoryDropdown from "../common/CategoryDropdown";
function mapStateToProps(state) {
  return {};
}

class ProjectForm extends Component {
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <Row className="animated fadeInDown">
            <TextInput
              s={12}
              l={12}
              m={12}
              xl={12}
              label="Name:"
              name="name"
              //   value={business.name}
              //   onChange={this.handleChange}
              required
            />

            <Textarea
              s={12}
              l={12}
              m={12}
              xl={12}
              label="Description"
              required
              name="description"
              data-length={200}
            />
               <StatusDropdown/>
               <CategoryDropdown/>

            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Price:"
              name="price"
              //   value={business.phone}
              //   onChange={this.handleChange}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Google Link:"
              name="googleLink"
              //   value={business.phone}
              //   onChange={this.handleChange}
              required
            />

            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Start Date"
              name="startDate"
              //   value={business.phone}
              //   onChange={this.handleChange}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Completed Date"
              name="completedDate"
              //   value={business.phone}
              //   onChange={this.handleChange}
            />

          </Row>
          <Row>
            <Button
              node="button"
              className="gradient-btn btn-primary"
              waves="green"
            >
              Save
            </Button>
          </Row>
        </form>
      </div>
    );
  }
}

export default connect(mapStateToProps)(ProjectForm);
