import React, { Component } from "react";
import { connect } from "react-redux";
import {TextInput, Row, Icon, Button} from 'react-materialize'
class EditContactForm extends Component {
    render() {
        return (
            <div>
                <form onSubmit={this.handleCreateContact}>
                    <Row>
                        <TextInput
                            s={12}
                            l={2}
                            m={2}
                            xl={2}
                            label='Name'
                            name='name'

                            required
                            onChange={this.handleInputChange}
                        />
                        <TextInput
                            s={12}
                            l={3}
                            m={3}
                            xl={3}
                            label='Phone'
                            name='phone'

                            required
                            onChange={this.handleInputChange}
                        />
                        <TextInput
                            s={12}
                            l={6}
                            m={4}
                            xl={4}
                            label='Email'
                            email
                            validate

                            name='email'
                            onChange={this.handleInputChange}
                        />
                        <Button
                            node='button'
                            type='submit'
                            waves='light'
                            style={{ marginTop: "20px" }}
                        >
                           Save
                            <Icon left>save</Icon>
                        </Button>
                        <Button
                            node='button'
                            type='submit'
                            waves='light'
                            style={{ marginTop: "20px" }}
                        >
                           Cancel

                        </Button>
                    </Row>
                </form>
            </div>
        );
    }
}

const mapStateToProps = state => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(EditContactForm);
