        //     localStorage.setItem('user', user.email)
        //     this.setState({
        //         loading: false
        //     })
        // })

    };

    render() {
        const {loading} = this.state
        return (
            <div style={{ marginTop: "70px" }}>
                <Row>
                    <Col m={4}></Col>
                    <Col m={4}>
                        <h3> Login</h3>
                    </Col>
                    <Col m={4}></Col>
                </Row>

                <form
                    method='POST'
                    onSubmit={this.handleSubmit}
                    className='col s12'
                >
                    <TextInput
                        name='email'
                        email
                        label='Email'
                        validate
                        required
                        onChange={this.handleChange}
                    />

                    <TextInput
                        label='Password'
                        name='password'
                        password
                        required
                        onChange={this.handleChange}
                    />
                    {!loading ? 
                    <Button node='button' waves='light'>
                        Login
                        <Icon right>send</Icon>
                    </Button> : <Loading/>}

                </form>
            </div>
        );
    }
}
export default Login;
