import React, { Component } from "react";
import { connect } from "react-redux";
import { Modal, Button, Row, TextInput } from "react-materialize";
import formatPhone from "../../utils/commons/FormatPhone";
import ProvinceDropdown from "../common/ProvinceDropdown";
import API from "../../utils/API/API";

function mapStateToProps(state) {
  return {};
}

class BusinessForm extends Component {
  state = {
    business: { name: "", address: "", phone: "", province: "" },
    edited: false,
    successMessage: "",
    errorMessage:""
  };

  componentDidMount() {
    if (this.props.business) {
      this.setState({
        business: this.props.business
      });
    }
  }

  handleChange = e => {
    const field = e.target.name;
    let value = e.target.value;

    if (field === "phone") {
      value = formatPhone(value);
    }

    let business = this.state.business;
    business[field] = value;
    this.setState({
      business,
      edited: true
    });
  };

  handleSubmit = e => {
    e.preventDefault();
    let { business } = this.state;
    this.setState({
      ...this.state,
      successMessage: "",
      errorMessage: "",
      loading: true,
      edited: false
    })
    if (!business._id) {
      API.post(`businesses`, business)
        .then(response => {
            // this.props.updateData();
            // this.props.closeCreateModal();
            this.setState({
          })
        })
        .catch(error => {
          console.log(error);
        });
    } else {
      const _id = business._id;
      const updateBusiness = {
        name: business.name,
        phone: business.phone,
        address: business.address,
        province: business.province
      };

      API.patch(`businesses/${_id}`, updateBusiness)
        .then(response => {
          //Update the data in the parent component
          this.props.updateData();
          this.setState({
            ...this.state,
            successMessage: "Save successfully!",
            errorMessage: "",
            loading: false,
            edited: false
          });
        })
        .catch(error => {
          console.log(error)
          this.setState({
            ...this.state,
            successMessage:"",
            errorMessage: "Cannot save the data!",
            loading: false,
            edited: false
          });
        });
    }
  };

  render() {
    const { business, edited } = this.state;
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <Row className="animated fadeInUp">
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Name:"
              name="name"
              value={business.name}
              onChange={this.handleChange}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Address"
              name="address"
              value={business.address}
              onChange={this.handleChange}
              required
            />
            <TextInput
              s={12}
              l={6}
              m={6}
              xl={6}
              label="Phone:"
              name="phone"
              value={business.phone}
              onChange={this.handleChange}
              required
            />
            <ProvinceDropdown
              handleChange={this.handleChange}
              province={business.province}
            />
          </Row>
          <Row>
            <Button
              node="button"
              className={edited ? "gradient-btn btn-primary" : ""}
              waves="green"
              disabled={!edited}
            >
              Save
            </Button>
          </Row>
        </form>
        <Row>
          {this.state.successMessage && (
            <Row className="animated shake">
              <p className="green-text animated bounceOutLeft delay-3s">
                {this.state.successMessage}
              </p>
            </Row>
          )}
          {this.state.errorMessage && (
            <Row className="animated rubberBand">
              <p className="red-text animated bounceOutLeft delay-3s ">
                {this.state.errorMessage}
              </p>
            </Row>
          )}
        </Row>
      </div>
    );
  }
}

export default connect(mapStateToProps)(BusinessForm);
