import React, { Component } from "react";
import { TextInput, Row, Col, Button, Icon } from "react-materialize";
import Loading from "../loading/Loading";
class Login extends Component {
    constructor(props) {
        super(props);
        this.sate = {
            email: "",
            password: "",
            loading: false,
        };
    }

    handleChange = (e) => {

       this.setState({
             ...this.state,
            [e.target.name]: e.target.value
    })
    };

    handleSubmit = e => {
        const {email, password} = this.state
        this.setState({
            loading: true
        })
        e.preventDefault();
        console.log(this.state)
        fetch(`http://localhost:3000/users`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                email,
                password,
                name: 'Ha Nguyen Phu',
                phone: '204 558 4030',
                role: 'admin'

            })
        }).then((res) => {
            console.log(res.body)
            this.setState({
                loading:false
            })
            console.log(this.state)
        })

    };

    render() {
        const {loading} = this.state
        return (
            <div style={{ marginTop: "70px" }}>
                <Row>
                    <Col m={4}></Col>
                    <Col m={4}>
                        <h3> Login</h3>
                    </Col>
                    <Col m={4}></Col>
                </Row>

                <form
                    method='POST'
                    onSubmit={this.handleSubmit}
                    className='col s12'
                >
                    <TextInput
                        name='email'
                        email
                        label='Email'
                        validate
                        required
                        onChange={this.handleChange}
                    />

                    <TextInput
                        label='Password'
                        name='password'
                        password
                        required
                        onChange={this.handleChange}
                    />
                    {loading ? 
                    <Button node='button' waves='light'>
                        Login
                        <Icon right>send</Icon>
                    </Button> : <Loading/>}

                </form>
            </div>
        );
    }
}
export default Login;
