import React, { Component } from "react";
import { connect } from "react-redux";
import {TextInput, Row, Icon, Button} from 'react-materialize'
import Loading from "../loading/Loading";
import Axios from "axios";
class EditContactForm extends Component {

    state = {

    }

    componentDidMount(){
        this.setState({
            contact: this.props.contact
        })
    }

    componentDidUpdate(prevProps) {
        if(prevProps.contact._id !== this.props.contact._id){
            this.setState({
                contact: this.props.contact
            })
        }
    }

    handleUpdateContact = (e) => {
        e.preventDefault()
        const contact = this.state.contact
        Axios.patch(`contacts/${contact._id}`, contact).then(response => {
            this.props.updateContact(response.data)
            this.props.closeEditContact()
        }).catch(error => {
            console.log(error)
        })

    }

    handleInputChange = (e) => {

        const phoneRegex = /^\(?([0-9]{3})\)?[-.]?([0-9]{3})[-.]?([0-9]{4})$/;
        const field = e.target.name;
        let value = e.target.value;

        if (field === "phone") {
            value = value.replace(phoneRegex, "($1)$2-$3");
            value = value.replace(" ", "");
            value = value.replace("+1", "");
            if (value.length > 9) {
                value = value.slice(0, 13);
            }
        }

        let contact = {
            _id: this.state.contact._id,
            name:this.state.contact.name,
            phone:this.state.contact.phone,
            email:this.state.contact.email,
            [field]: value
        }
        this.setState({
            contact
        });
    }

    render() {
        if(!this.state.contact) {
            return <Loading/>
        }
        const {contact} = this.state
        return (
            <div>
                <form onSubmit={this.handleUpdateContact}>
                    <Row>
                        <h5>Edit Contact</h5>
                        <TextInput
                            s={12}
                            l={2}
                            m={2}
                            xl={2}
                            label='Name'
                            name='name'
                            value={contact.name}
                            required
                            onChange={this.handleInputChange}
                        />
                        <TextInput
                            s={12}
                            l={3}
                            m={3}
                            xl={3}
                            label='Phone'
                            name='phone'
                            value={contact.phone}
                            required
                            onChange={this.handleInputChange}
                        />
                        <TextInput
                            s={12}
                            l={6}
                            m={4}
                            xl={4}
                            label='Email'
                            email
                            validate
                            value={contact.email}
                            name='email'
                            onChange={this.handleInputChange}
                        />
                        <Button
                            node='button'
                            type='submit'
                            waves='light'
                            style={{ marginTop: "20px" }}
                        >
                           Save
                            <Icon left>save</Icon>
                        </Button>

                    </Row>
                </form>
            </div>
        );
    }
}

const mapStateToProps = state => ({});

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(EditContactForm);
