import React, { Component } from "react";
import { connect } from "react-redux";
import { Col, Row, Table, TextInput, Select, Icon, Navbar, NavItem } from "react-materialize";
import Button from "react-materialize/lib/Button";
import Loading from "../loading/Loading";
import axios from "axios";
import { fetch_province } from "../../redux/actions/index";

const mapStateToProps = state => {
    return { provinces: state.provinces };
};

function mapDispatchToProps(dispatch) {
    return {
        addProvinceToRedux: provinces => dispatch(fetch_province(provinces))
    };
}

class Business extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchBy: {
                name: "",
                phone: "",
                province: ""
            },
            provinces: [],
            loading: true
        };
    }

    componentDidMount() {
        this.getData();
    }

    includeTokenWithRequest() {
        const token = localStorage.getItem("token");
        axios.defaults.baseURL = "http://localhost:3000/";
        axios.defaults.headers.common = { Authorization: `Bearer ${token}` };
    }

    getData() {
        this.includeTokenWithRequest();
        const provinceRequest = axios.get(`provinces`);
        const businessRequest = axios.get(`businesses`);

        axios
            .all([provinceRequest, businessRequest])
            .then(
                axios.spread((...responses) => {
                    const provinces = responses[0].data;
                    const businesses = responses[1].data;

                    this.setState({
                        provinces,
                        businesses,
                        loading: false
                    });
                    //Save provinces data in redux for later usage
                    this.props.addProvinceToRedux(provinces);
                })
            )
            .catch(error => {
                console.log(error);
            });
    }

    handleSubmit = e => {
        e.preventDefault();
    };

    handleInputChange = e => {
        const searchBy = { ...this.state.searchBy };

        const field = e.target.name;
        const value = e.target.value;
        searchBy[field] = value;

        this.setState({
            ...this.state,
            searchBy
        });
    };

    getProvinceName = _id => {
        const { provinces } = this.state;

        const foundProvince = provinces.filter(
            province => province._id === _id
        );

        return foundProvince[0].name;
    };

    render() {
        const { name, phone, province } = this.state.searchBy;
        const { loading, provinces, businesses } = this.state;

        if (loading) {
            return <Loading />;
        }
        return (
            <div>
                <Row>
                    <Navbar
                        alignLinks='right'
                        brand={
                            <a className='brand-logo' href='#'>
                                Logo
                            </a>
                        }
                        menuIcon={<Icon>menu</Icon>}
                        options={{
                            draggable: true,
                            edge: "left",
                            inDuration: 250,
                            onCloseEnd: null,
                            onCloseStart: null,
                            onOpenEnd: null,
                            onOpenStart: null,
                            outDuration: 200,
                            preventScrolling: true
                        }}
                    >
                        <NavItem href=''>Getting started</NavItem>
                        <NavItem href='components.html'>Components</NavItem>
                    </Navbar>
                </Row>
                <Row
                    style={{
                        backgroundColor: "white",
                        padding: "30px",
                        marginTop: "20px",
                        boxShadow: "1px 1px 1px #9E9E9E"
                    }}
                >
                    <form>
                        <h6>Filter by</h6>
                        <Col s={12} m={12} l={12} xl={12}>
                            <TextInput
                                s={12}
                                l={4}
                                m={4}
                                xl={4}
                                label='Name'
                                name='name'
                                value={name}
                                onChange={this.handleInputChange}
                            />
                            <TextInput
                                s={12}
                                l={3}
                                m={3}
                                xl={3}
                                label='Phone'
                                name='phone'
                                value={phone}
                                onChange={this.handleInputChange}
                            />
                            <Select
                                onChange={this.handleInputChange}
                                options={{
                                    classes: "",
                                    dropdownOptions: {
                                        alignment: "left",
                                        autoTrigger: true,
                                        closeOnClick: true,
                                        constrainWidth: true,
                                        container: null,
                                        coverTrigger: true,
                                        hover: false,
                                        inDuration: 150,
                                        onCloseEnd: null,
                                        onCloseStart: null,
                                        onOpenEnd: null,
                                        onOpenStart: null,
                                        outDuration: 250
                                    }
                                }}
                                value={province}
                                name='province'
                            >
                                <option disabled value=''>
                                    Select Province
                                </option>
                                {provinces.map(province => {
                                    return (
                                        <option
                                            className='black-text'
                                            key={province._id}
                                            value={province._id}
                                        >
                                            {province.name}
                                        </option>
                                    );
                                })}
                            </Select>
                            <Button
                                node='button'
                                type='submit'
                                style={{
                                    marginRight: "5px",
                                    bottom: "-20px"
                                }}
                                waves='light'
                            >
                                Search
                            </Button>
                        </Col>
                    </form>
                </Row>
                <Row
                    style={{
                        backgroundColor: "white",
                        padding: "20px",
                        boxShadow: "1px 1px 1px #9E9E9E"
                    }}
                >
                    <h6>Businesses</h6>
                    <Col
                        s={12}
                        m={12}
                        l={12}
                        xl={12}
                        style={{ marginTop: "50px" }}
                    >
                        <Table>
                            <thead>
                                <tr>
                                    <th data-field='id'>Name</th>
                                    <th data-field='price'>Phone</th>
                                    <th data-field='price'>Province</th>
                                    <th data-field='price'>Details</th>
                                </tr>
                            </thead>
                            <tbody>
                                {businesses.map(business => {
                                    return (
                                        <tr key={business._id}>
                                            <td>{business.name}</td>
                                            <td>{business.phone}</td>
                                            <td>
                                                {this.getProvinceName(
                                                    business.province
                                                )}
                                            </td>
                                            <td>
                                                <a href='#'>
                                                    <Icon>launch</Icon>
                                                </a>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </Table>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Business);
