import React, { Component } from "react";
import { TextInput, Row, Col, Button, Icon } from "react-materialize";
class Login extends Component {
    constructor(props) {
        super(props);
        this.sate = {
            email: "",
            password: ""
        };
    }

    handleChange = (e) => {
       console.log(e.target.value); 
       this.setState({
            value: e.target.value
    })
    };

    handleSubmit = e => {
        e.preventDefault();

    };

    render() {
        const {email, password} = this.state
        return (
            <div style={{ marginTop: "70px" }}>
                <Row>
                    <Col m={4}></Col>
                    <Col m={4}>
                        <h3> Login</h3>
                    </Col>
                    <Col m={4}></Col>
                </Row>

                <form
                    method='POST'
                    onSubmit={this.handleSubmit}
                    className='col s12'
                >
                    <TextInput
                        name='email'
                        email
                        label='Email'
                        validate
                        required
                        value={email}
                        onChange={this.handleChange}
                    />

                    <TextInput
                        label='Password'
                        name='password'
                        value={password}
                        password
                        required
                    />

                    <Button node='button' waves='light'>
                        Login
                        <Icon right>send</Icon>
                    </Button>
                </form>
            </div>
        );
    }
}
export default Login;
