import React, { Component } from "react";
import { connect } from "react-redux";
import { TextInput, Row, Select, Button } from "react-materialize";
import phoneFormat from "../../../utils/commons/FormatPhone";
import API from "../../../utils/API/API";

function mapStateToProps(state) {
  return {};
}

class UserForm extends Component {
  state = {
    user: {
      name: "",
      phone: "",
      email: "",
      role: "user",
      password: "123456789",
      repeatPassword: "123456789"
    },
    edited: false,
    successMessage:"",
    errorMessage:""
  };
  handleChange = e => {
    const field = e.target.name;
    let value = e.target.value;
    if (field === "phone") {
      value = phoneFormat(value);
    }
    let user = this.state.user;
    user[field] = value;
    this.setState({
      user,
      edited: true
    });
  };

  handleSubmit = (e) => {
      e.preventDefault()
      const {user} = this.state
      if(user.password !== user.repeatPassword){
          this.setState({
            errorMessage: "Passwords do not match!"
          })
      } else {
          delete user.repeatPassword
          API.post(`/users`, user).then(response => {
              console.log(response.data)
          }).catch(error => {
            this.setState({
                errorMessage: "An error has been occurred!"
              })
          })
      }
  }
  render() {
    const { user, edited } = this.state;
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <Row>
            <TextInput
              label="Name"
              name="name"
              s={12}
              m={6}
              xl={6}
              l={6}
              onChange={this.handleChange}
              value={user.name}
              required
            />
            <TextInput
              label="Email"
              name="email"
              validate
              email
              s={12}
              m={6}
              xl={6}
              l={6}
              onChange={this.handleChange}
              value={user.email}
              required
            />
            <TextInput
              label="Phone"
              s={12}
              m={6}
              xl={6}
              l={6}
              name="phone"
              onChange={this.handleChange}
              value={user.phone}
              required
            />
            <Select
              s={12}
              m={6}
              xl={6}
              l={6}
              label="Role"
              name="role"
              onChange={this.handleChange}
              options={{
                classes: "",
                dropdownOptions: {
                  alignment: "left",
                  autoTrigger: true,
                  closeOnClick: true,
                  constrainWidth: true,
                  container: null,
                  coverTrigger: true,
                  hover: false,
                  inDuration: 150,
                  onCloseEnd: null,
                  onCloseStart: null,
                  onOpenEnd: null,
                  onOpenStart: null,
                  outDuration: 250
                }
              }}
              value={user.role}
              required
            >
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </Select>
            <TextInput
              label="Password"
              s={12}
              m={6}
              xl={6}
              l={6}
              name="password"
              password
              onChange={this.handleChange}
              value={user.password}
              required
            />
            <TextInput
              label="Repeat Password"
              s={12}
              m={6}
              xl={6}
              l={6}
              name="repeatPassword"
              password
              onChange={this.handleChange}
              value={user.repeatPassword}
              required
            />
          </Row>
          <Row>
            <Button
              node="button"
              className={edited ? "gradient-btn btn-primary" : ""}
              waves="green"
              disabled={!edited}
            >
              Save
            </Button>
          </Row>
        </form>
        <Row>
          {this.state.successMessage && (
            <Row className="animated shake">
              <p className="green-text animated bounceOutLeft delay-3s">
                {this.state.successMessage}
              </p>
            </Row>
          )}
          {this.state.errorMessage && (
            <Row className="animated rubberBand">
              <p className="red-text animated bounceOutLeft delay-3s ">
                {this.state.errorMessage}
              </p>
            </Row>
          )}
        </Row>
      </div>
    );
  }
}

export default connect(mapStateToProps)(UserForm);
