import React, { Component } from "react";
import { Row, Col, TextInput, Button, Icon } from "react-materialize";
import { connect } from "react-redux";
import Loading from "../loading/Loading";
const mapStateToProps = state => {
    return { user: state.user };
};

class Profile extends Component {

    state = {
        user: {
            name: '',
            email: '',
            phone: '',
            currentPassword: '',
            newPassword:'',
            repeatPassword:''
        },
        errMessage: ""
    }

    componentDidMount() {
        const user = this.props.user
        this.setState( prevState => ({
            ...prevState,
            user: {
                ...this.state.user, 
                email: user.email,
                name: user.name,
                phone:user.phone
            }
        }))
    }

    handleInputChange = (e) => {
        const user = {...this.state.user}
        const field = e.target.name 
        const newValue = e.target.value 
        user[field] = newValue
        this.setState({user})
    }

    handleSubmit = (e) => {
        e.preventDefault()
        const newUserData = this.state.user
        if(newUserData.newPassword !== newUserData.repeatPassword){
            this.setState({
                ...this.state,
                errMessage: "Password not match!"
            })
        }
    }

    render() {
        if(!this.state.user) {
            return <Loading/>
        }
        const { user, errMessage } = this.state;

        return (
            <div>
                <h5>User Profile</h5>
                <form onSubmit={this.handleSubmit}>
                    <Row>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                s={12}
                                l={12}
                                m={12}
                                label='Name'
                                value={user.name}
                                name='name'
                                onChange={this.handleInputChange}
                            />
                        </Col>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                email
                                validate
                                disabled
                                s={12}
                                l={12}
                                m={12}
                                label='Email'
                                value={user.email}
                                readOnly
                            />
                        </Col>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                s={12}
                                l={12}
                                m={12}
                                label='Phone'
                                name='phone'
                                value={user.phone}
                                onChange={this.handleInputChange}
                            />
                        </Col>
                    </Row>
                    <Row>
                        <h5>Change Password</h5>
                        <Col s={12} m={12} l={12} xl={12}>
                            <TextInput
                                password
                                s={12}
                                l={12}
                                m={12}
                                label='Current Password'
                                name='currentPassword'
                                value={user.currentPassword}
                                onChange={this.handleInputChange}
                            />
                        </Col>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                password
                                s={12}
                                l={12}
                                m={12}
                                label='New Password'
                                name='newPassword'
                                value={user.newPassword}
                                onChange={this.handleInputChange}
                            />
                        </Col>
                        <Col s={12} m={6} l={6} xl={6}>
                            <TextInput
                                password
                                s={12}
                                l={12}
                                m={12}
                                label='Repeat Password'
                                name='repeatPassword'
                                value={user.repeatPassword}
                                onChange={this.handleInputChange}
                            />
                        </Col>
                    </Row>
                    <Row>
                        <p className="red-text">{errMessage || ''}</p>
                    </Row>
                    <Row>
                        <Button node='button' type='submit' waves='light'>
                            Update
                            <Icon right>send</Icon>
                        </Button>
                    </Row>
                </form>
            </div>
        );
    }
}

export default connect(mapStateToProps)(Profile);
