import React, { Component } from "react";
import { TextInput, Row, Col, Button, Icon } from "react-materialize";
import Loading from "../loading/Loading";
import axios from "axios";

import {authenticate} from "../../redux/actions/index"
import { connect } from "react-redux";

function mapDispatchToProps(dispatch) {
    return {
        authenticate: authenticated => dispatch(authenticate(authenticated))
    }
}

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            password: "",
            loading: false,
            errorMessage: ""
        };
    }

    handleChange = e => {
        this.setState({
            ...this.state,
            [e.target.name]: e.target.value
        });
    };

    handleSubmit = e => {
        const { email, password } = this.state;
        this.setState ({
            email: '',
            password: ''
        })
        e.preventDefault();
        // const user = {
        //     email,
        //     password
        // }
        // this.props.authenticate(user)
        console.log(this.state)
        axios
            .post(`http://localhost:3000/users/login`, {
                email,
                password,
            })
            .then(response => {

                        response.data.authenticated = true

                        if(response.data) {
                            this.props.authenticate(response.data)
                            this.setState({
                                loading: false,
                                errorMessage: ''
                            });
                            localStorage.setItem('user', response.data.user.email)
                            localStorage.setItem('token', response.data.token)
                        }


            })
            .catch(error => {
                console.log(error)
                this.setState({
                    errorMessage: 'Incorrect Email or Password',
                    loading: false
                });
            });
    };

    render() {
        const { loading, errorMessage } = this.state;
        return (
            <div style={{ marginTop: "70px" }}>
                <Row>
                    <Col m={4}></Col>
                    <Col m={4}>
                        <h3> Login</h3>
                    </Col>
                    <Col m={4}></Col>
                </Row>

                <form
                    method='POST'
                    onSubmit={this.handleSubmit}
                    className='col s12'
                >
                    <TextInput
                        name='email'
                        email
                        label='Email'
                        validate
                        required
                        onChange={this.handleChange}
                    />

                    <TextInput
                        label='Password'
                        name='password'
                        password
                        required
                        onChange={this.handleChange}
                    />

                      {errorMessage && <div className="red-text text-accent-2">
                        {errorMessage}
                    </div>}
                    <br />
                    {!loading ? (
                        <Button node='button' waves='light'>
                            Login
                            <Icon right>send</Icon>
                        </Button>
                    ) : (
                        <Loading />
                    )}

                </form>
            </div>
        );
    }
}
export default connect(null, mapDispatchToProps)(Login);
