import React, { Component } from "react";
import { connect } from "react-redux";
import {
    Modal,
    Button,
    Row,
    Col,
    TextInput,
    Select,
    Table, Icon
} from "react-materialize";
import Loading from "../loading/Loading";
import axios from "axios";
import ContactForm from "../contact/ContactForm";
import { EEXIST } from "constants";
import { ContactTable } from "../contact/ContactTable";

class BusinessModalDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
        };
    }

    componentDidMount() {
        this.setState({
            business: this.props.business
        });
        this.getBusinessDetail(this.props.business._id);
    }

    componentDidUpdate(prevProps) {
        if(prevProps.business._id !== this.props.business._id){
            this.getBusinessDetail(this.props.business._id)
            this.setState({
                business: this.props.business,
                errorMessage:'',
                successMessage:''
            })
        }
    }

    getBusinessDetail(id) {
        this.setState({
            ...this.state,
            loading: true
        });
        axios
            .get(`businesses/${id}`)
            .then(response => {
                if (response.data.businesses) {
                    this.setState({
                        ...this.state,
                        contacts: response.data.businesses.contacts,
                        province: response.data.businesses.province,
                        loading: false
                    });
                }
            })
            .catch(err => {
                this.setState({
                    loading: false,
                    errorMessage:'Could not fetch the data',
                });
            });
    }

    handleChangeBusiness = e => {
        const field = e.target.name
        const value = e.target.value 

        let business = this.state.business
        business[field] = value
        this.setState({
            ...this.state,
            business
        })
    };

    updateBusiness = e => {
        e.preventDefault()
        const _id = this.state.business._id
        const business = {
            name: this.state.business.name,
            phone: this.state.business.phone,
            address: this.state.business.address,
            province: this.state.business.province
        }
        this.setState({
            errorMessage:'',
            successMessage:'',
            loading: true,
        })
        axios.patch(`businesses/${_id}`, business).then(response => {
            //Update the data in the parent component
          this.props.updateData()
          this.setState({
              ...this.state, 
              successMessage: 'Save successfully!',
              loading: false
          })
        }).catch(error => {
            this.setState({
                errorMessage: 'Cannot save the data!',
                loading: false
            })
        })
    }


    openCreateContactForm = () => {
        this.setState({
            openContactForm: true
        })
    }

    displayProvince(_id) {
        const { provinces } = this.props;
        const province = provinces.filter(province => province._id === _id)[0];
        return (
            <Select
                options={{
                    classes: "",
                    dropdownOptions: {
                        alignment: "left",
                        autoTrigger: true,
                        closeOnClick: true,
                        constrainWidth: true,
                        container: null,
                        coverTrigger: true,
                        hover: false,
                        inDuration: 150,
                        onCloseEnd: null,
                        onCloseStart: null,
                        onOpenEnd: null,
                        onOpenStart: null,
                        outDuration: 250
                    }
                }}
                onChange={this.handleChangeBusiness}
                value={province._id}
                name='province'
            >
                <option disabled value=''>
                    Select Province
                </option>
                {provinces.map(province => {
                    return (
                        <option
                            className='black-text'
                            key={province._id}
                            value={province._id}
                        >
                            {province.name}
                        </option>
                    );
                })}
            </Select>
        );
    }

    render() {

        const contacts = this.state.contacts || [];
        if(!this.state.business) {
            return <Loading/>
        }
        const { business } = this.state;
        return (
            <Modal
                actions={[
                    <Button flat modal='close' node='button' waves='green'>
                        Close
                    </Button>
                ]}
                open={true}
                style={{ boder: "none" }}
                bottomSheet={false}
                fixedFooter={false}
                header='Business Detail'
                id='modal1'
                required
                options={{
                    dismissible: true,
                    endingTop: "10%",
                    inDuration: 250,
                    onCloseEnd: null,
                    onCloseStart: null,
                    onOpenEnd: null,
                    onOpenStart: null,
                    opacity: 0.5,
                    outDuration: 250,
                    preventScrolling: true,
                    startingTop: "4%"
                }}
            >
                {!this.state.loading ? (
                    <div>
                        <form onSubmit={this.updateBusiness}>
                            <Row>
                                <TextInput
                                    s={12}
                                    l={6}
                                    m={6}
                                    xl={6}
                                    label='Name:'
                                    value={business.name}
                                    name='name'
                                    onChange={this.handleChangeBusiness}
                                    required
                                />
                                <TextInput
                                    s={12}
                                    l={6}
                                    m={6}
                                    xl={6}
                                    label='Address'
                                    name='address'
                                    value={business.address}
                                    onChange={this.handleChangeBusiness}
                                    required
                                />
                                <TextInput
                                    s={12}
                                    l={6}
                                    m={6}
                                    xl={6}
                                    label='Phone:'
                                    name='phone'
                                    value={business.phone}
                                    onChange={this.handleChangeBusiness}
                                    required
                                />

                                {this.displayProvince(business.province)}
                            </Row>
                            <Row>
                                <Button node='button' waves='green'>
                                    Update
                                </Button>
                            </Row>
                        </form>
                        {this.state.successMessage && <Row>
                            <p className="green-text">{this.state.successMessage}</p>
                        </Row>}
                        {this.state.errorMessage && <Row>
                            <p className="red-text">{this.state.errorMessage}</p>
                        </Row>}

                        <Row>
                            <h5>Contacts <a href="#" onClick={this.openCreateContactForm}><Icon>add_circle</Icon></a></h5>
                            {this.state.openContactForm && <ContactForm/>}
                        </Row>
                        <ContactTable contacts={contacts}/>

                    </div>
                ) : (
                    <Loading />
                )}
            </Modal>
        );
    }
}

const mapStateToProps = state => {
    return { provinces: state.provinces };
};

const mapDispatchToProps = {};

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(BusinessModalDetail);
