import { AUTHENTICATE }  from '../constants/action-types'

const initialState = {
    authenticated: false,
}

function rootReducer(state = initialState, action) {
    if(action.type === AUTHENTICATE) {
       return Object.assign({}, state, {
           ...state,
           authenticated: true,
           email: '',
           name:'',
           phone: ''
       })
    }
    return state
}


export default rootReducer
